package com.zapapp.jaas.authentication;import java.io.Serializable;import java.security.Principal;public class MyPrincipal implements Principal , Serializable{	private String principalName;		public MyPrincipal (String name)	{		this.principalName = name;	}		public boolean equals (Object another)	{		if (null != another)		{			if (another instanceof MyPrincipal)			{				MyPrincipal myPrincipal	= (MyPrincipal)another;				if (myPrincipal.getName().equalsIgnoreCase (this.principalName) ) 					{					return true;				}			}					}				return false;	}		public String getName ()	{		return principalName;	}		public int hashCode ()	{		return principalName.hashCode ();	}		public String toString ()	{		return principalName;	}}